	<script type="text/javascript">
       $(document).ready( function(){
		// Contact form script 
		var frm = $('#hp-form');
		frm.submit(function (e) {
			e.preventDefault();

			$.ajax({
				type: frm.attr('method'),
				url: frm.attr('action'),
				data: frm.serialize(),
				success: function (data) {
					$('#hp-form-success .info-wrap p').text('The information you shared was successfully submitted. Thank you, we will get back to you soon.');
					$('#hp-form-success').modal({
						keyboard: false,
						show: 'true'
					});
					//reset form
					$("#hp-form")[0].reset();
				},
				error: function (data) {
					
				},
			});
		});

		// Newsletter form script 
		var frm_ns = $('#hp-ns-form');
		frm_ns.submit(function (e) {
			e.preventDefault();

			$.ajax({
				type: frm_ns.attr('method'),
				url: frm_ns.attr('action'),
				data: frm_ns.serialize(),
				success: function (data) {
					$('#hp-form-success .info-wrap p').text('Thank you for subscribing to our newsletter.');
					$('#hp-form-success').modal({
						keyboard: false,
						show: 'true'
					});
					//reset form
					$("#hp-ns-form")[0].reset();
				},
				error: function (data) {
					
				},
			});
		});
	   });
    </script>
